import Timer from '../components/Timer'
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';


const WritingPage = () => {
    const [textInput,setTextInput] = useState("");
    const [wordCount,setWordCount] = useState(0);
  return (
    
    <div>
        <div className="mb-3">
  <label for="exampleFormControlTextarea1" className="form-label"> Typing Speed Test</label>
  <textarea className="form-control" id="exampleFormControlTextarea1" rows="5"></textarea>
</div>
      <div className="mb-3">
  <label for="exampleFormControlInput1" className="form-label">write here</label>
  <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="" value={textInput} onChange={(e) => setTextInput(e.target.value)} /> 
  <Timer/>
  <p> <b>{textInput.split("")} words</b></p>
</div>

    </div>
  )
}

export default WritingPage
