import React, { useState, useEffect } from 'react';

const timerContainer = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  height: "10vh",
};

const timer = {
  fontSize: '3em',
  backgroundColor: 'blue',
  color: 'black',
  padding: '20px',
  borderRadius: '10px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
};

const Timer = ({onTimerComplete}) => {
  const [seconds, setSeconds] = useState(60);
  


  useEffect(() => {
    const interval = setInterval(() => {
      setSeconds((prevSeconds) => {
        if (prevSeconds > 0) {
          return prevSeconds - 20;
        } else {
          // Reset the timer to its initial value
          onTimerComplete(0); // Notify the parent component
          return 60; // Replace 'initialSeconds' with your actual initial value
        }
      });
    }, 1000);
  
    return () => clearInterval(interval);
  }, [onTimerComplete]);


  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const remainingSeconds = time % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  return (
    <div className="timer-container" style={timerContainer}>
      <div className="timer" style={timer}>
        {formatTime(seconds)}
      </div>
    </div>
  );
};

export default Timer;
