import React, { useState, useEffect, useCallback } from 'react';
import Timer from '../components/Timer';
import 'bootstrap/dist/css/bootstrap.min.css';
import Modal from 'react-modal';

const WritingPage = () => {
  const [textInput, setTextInput] = useState("");
  
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isStarted, setIsStarted] = useState(false);
  const [myWord, setMyWord] = useState("");
  const [incorrectCount, setIncorrectCount] = useState(0);
  const [characters,setCharacters] = useState(0)
  const generateRandomText = useCallback(() => {
    const words = [
      "Game", "is", "my", "sit", "Name", "constructor",
      "adipiscing", "chocolate", "sed", "do", "termper", "bestfriend",
      "Fight", "in", "manufacture", "eneyearfeast", "money", "magna", "aliqua"
    ];

    const randomText = Array.from({ length: 250 }, () => words[Math.floor(Math.random() * words.length)]);
    return randomText.join(" ");
  }, []); 

  useEffect(() => {
    if (isStarted) {
      setMyWord(generateRandomText());
      setIncorrectCount(0);
      setCharacters(0);
    }
  }, [isStarted, generateRandomText]);
  const handleKeyDown = (e) => {
    // Prevent the default behavior for the backspace and delete keys
    if (e.keyCode === 8 || e.keyCode === 46) {
      e.preventDefault();
    }
  };

  const handleChange = (e) => {
    // If isStarted is true, don't allow changes
    if (isStarted) {
      e.preventDefault();
    } else {
      // Update the text input value
      setTextInput(e.target.value);
    }
  };
  useEffect(() => {
    let count = 0;
    let count1 = 0;
    let init = textInput.length -1;
    if (isStarted) {
      for (let i = init; i <= init; i++) {
        if (myWord[i] !== textInput[i] && myWord[i] !== ' ' && textInput[i] !== ' ') {
          count++;
        } else {
          if ( myWord[i] === textInput[i]) {
            count1++;
          }
        }
      }
  
      setCharacters((prevCount1) => prevCount1 + count1 );
      setIncorrectCount(count);
    }
  }, [isStarted, myWord, textInput]);

  const renderColoredWord = () => {
    const minLength = Math.max(myWord.length, textInput.length);

    return Array.from({ length: minLength }, (_, i) => {
      const isSpace = myWord[i] === ' ';

      const style = {
        color: isSpace ? 'blue' : myWord[i] === textInput[i] ? 'green' : 'red',
        display: 'inline-block',
      };

      return (
        <span key={i} style={style}>
          {isSpace ? '\u00A0' : myWord[i]}
        </span>
      );
    });
  };

 

  const handleTimerComplete = (value) => {
    

    if (value === 0) {
      setIsModalOpen(true);
      setIsStarted(false);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleStart = () => {
    setIsStarted(true);
  };

  return (
    <div>
      
      
    <div>
      <div className="mb-3">
        <label htmlFor="exampleFormControlTextarea1" className="form-label">Typing Speed Test</label>
        <div>
          {renderColoredWord()}
        </div>
      </div>
       
      </div>
      <div className="mb-3">
        <label htmlFor="exampleFormControlInput1" className="form-label">Write here</label>
        <input
          type="email"
          className="form-control"
          id="exampleFormControlInput1"
          placeholder=""
          value={isStarted ? textInput : ""}
          disabled={!isStarted}
          onChange={(e) => setTextInput(e.target.value)}
        />
        <div>
          {isStarted && <Timer isStarted={isStarted} onTimerComplete={handleTimerComplete} />}
        </div>
        <b>{characters} characters, {Math.ceil(characters / 4.7)} words</b>
        {isStarted && <p>Incorrect characters: {incorrectCount}</p>}
        {!isStarted && <button onClick={handleStart}>Start Typing Test</button>}
      </div>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Time out"
        style={{
          content: {
            height: '200px',
            width: '400px',
            margin: 'auto', 
          },
          overlay: {
            background: 'rgba(0, 0, 0, 0.3)', 
          },
        }}
      >
        <h2>Timer has reached 0!</h2>
        <button onClick={closeModal}>Close Modal</button>
      </Modal>
    </div>
  );
};

export default WritingPage;
