import React, { useState, useEffect, useCallback } from 'react';
import TypingArea from '../components/TypingArea';
import TextInput from '../components/TextInput';
import TimerSection from '../components/TimerSection';
import 'bootstrap/dist/css/bootstrap.min.css';
import Modal from 'react-modal';


const WritingPage = () => {
  const [textInput, setTextInput] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isStarted, setIsStarted] = useState(false);
  const [myWord, setMyWord] = useState("");
  const [incorrectCount, setIncorrectCount] = useState(0);
  const [characters, setCharacters] = useState(0);

  const generateRandomText = useCallback(() => {
    // ... (unchanged)
  }, []); 

  useEffect(() => {
    if (isStarted) {
      setMyWord(generateRandomText());
      setIncorrectCount(0);
      setCharacters(0);
    }
  }, [isStarted, generateRandomText]);

  const handleInputChange = (value) => {
    if (isStarted) {
      setTextInput(value);
    }
  };

  // ... (unchanged)

  return (
    <div>
    <TypingArea myWord={myWord} textInput={textInput} />
    <TextInput isStarted={isStarted} textInput={textInput} onInputChange={handleInputChange} />
    <TimerSection
      isStarted={isStarted}
      onTimerComplete={handleTimerComplete} // pass handleTimerComplete
      characters={characters}
      incorrectCount={incorrectCount}
      onHandleStart={handleStart} // pass handleStart
    />
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Time out"
        style={{
          content: {
            height: '200px',
            width: '400px',
            margin: 'auto', 
          },
          overlay: {
            background: 'rgba(0, 0, 0, 0.3)', 
          },
        }}
      >
        <h2>Timer has reached 0!</h2>
        <button onClick={closeModal}>Close Modal</button>
      </Modal>
    </div>
  );
};

export default WritingPage;
