import React from 'react';
import Timer from './Timer';

const TimerSection = ({ isStarted, onTimerComplete, characters, incorrectCount, onHandleStart }) => {
  return (
    <div className="mb-3">
      {isStarted && <Timer isStarted={isStarted} onTimerComplete={onTimerComplete} />}
      <b>{characters} characters, {Math.ceil(characters / 4.7)} words</b>
      {isStarted && <p>Incorrect characters: {incorrectCount}</p>}
      {!isStarted && <button onClick={onHandleStart}>Start Typing Test</button>}
    </div>
  );
};

export default TimerSection;
