import React, { useState } from 'react';
import Timer from '../components/Timer';
import 'bootstrap/dist/css/bootstrap.min.css';
import Modal from 'react-modal';

const WritingPage = () => {
  const [textInput, setTextInput] = useState("");
  const [toPassValue, setToPassValue] = useState();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isStarted, setIsStarted] = useState(false);
  const [myWord, setMyWord] = useState("");
  const handleTimerComplete = (value) => {
    setToPassValue(value);
    
    if (value === 0) {
      // Open the modal when toPassValue is 0
      setIsModalOpen(true);
      setIsStarted(false);
      setMyWord(generateRandomText());
    }
  };
  
  const generateRandomText = () => {
    // Function to generate random text of 250 words
    // You can customize this function based on your requirements
    const words = [
      "Game", "is", "my", "sit", "Name", "constructor",
      "adipiscing", "chocolate", "sed", "do", "termper", "bestfriend",
      "Fight", "in", "manufacture", "eneyearfeast", "money", "magna", "aliqua"
    ];

    const randomText = Array.from({ length: 250 }, () => words[Math.floor(Math.random() * words.length)]);
    return randomText.join(" ");
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleStart = () => {
    setIsStarted(true);
    setMyWord(generateRandomText());
  };

  return (
    <div>
      <div className="mb-3">
        <label htmlFor="exampleFormControlTextarea1" className="form-label">Typing Speed Test</label>
        <textarea className="form-control" id="exampleFormControlTextarea1" rows="5" value = {myWord} disabled = {true}></textarea>
      </div>
      <div className="mb-3">
        <label htmlFor="exampleFormControlInput1" className="form-label">Write here</label>
        <input
          type="email"
          className="form-control"
          id="exampleFormControlInput1"
          placeholder=""
          value={textInput}
          disabled={!isStarted}
          onChange={(e) => setTextInput(e.target.value)}
        />
        <div>
        {isStarted && <Timer isStarted={isStarted} onTimerComplete={handleTimerComplete} />}
        </div>
        <b>{textInput.split("").length} characters and {Math.ceil(textInput.split("").length / 5)} words</b>
        {!isStarted && <button onClick={handleStart}>Start Typing Test</button>}
      </div>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Time out"
        style={{
          content: {
            height: '200px',
            width: '400px',
            margin: 'auto', // Center the modal
          },
          overlay: {
            background: 'rgba(0, 0, 0, 0.3)', // Semi-transparent overlay
          },
        }}
      >
        <h2>Timer has reached 0!</h2>
        <button onClick={closeModal}>Close Modal</button>
      </Modal>
    </div>
  );
};

export default WritingPage;
