import React from 'react'
import Timer from '../components/Timer'
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';


const WritingPage = () => {
    const [textInput,setTextInput] = useState("");
    const [dataFromChild, setDataFromChild] = useState(null);

  // Function to receive data from the child
  const handleDataFromChild = (data) => {
    // Do something with the data in the parent component
    console.log('Data received from child:', data);
    setDataFromChild(data);
  };
    
  return (
    
    <div>
        <div className="mb-3">
  <label for="exampleFormControlTextarea1" className="form-label"> Typing Speed Test</label>
  <textarea className="form-control" id="exampleFormControlTextarea1" rows="5"></textarea>
</div>
      <div className="mb-3">
  <label for="exampleFormControlInput1" className="form-label">write here</label>
  <input type="email" className="form-control" disabled={dataFromChild===0?true:false} id="exampleFormControlInput1" placeholder="" value={textInput} onChange={(e) => setTextInput(e.target.value)} /> 
  <Timer secondsToBeSend={{handleDataFromChild}}/>
  
  <b>{textInput.split("").length} characters  and  {Math.ceil(textInput.split("").length/5)} words</b>
</div>

    </div>
  )
}

export default WritingPage
