import React from 'react'
import Timer from '../components/Timer'
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';


import Modal from 'react-modal';
const WritingPage = () => {
    const [textInput,setTextInput] = useState("");
    const [toPassValue, setToPassValue] = useState();
    const [isModalOpen, setIsModalOpen] = useState(false);
  
    const handleTimerComplete = (value) => {
      setToPassValue(value);
  
      if (value === 0) {
        // Open the modal when toPassValue is 0
        setIsModalOpen(true);
      }
    };
  
    const closeModal = () => {
      setIsModalOpen(false);
    };
    
  return (
    
    <div>
        <div className="mb-3">
  <label for="exampleFormControlTextarea1" className="form-label"> Typing Speed Test</label>
  <textarea className="form-control" id="exampleFormControlTextarea1" rows="5"></textarea>
</div>
      <div className="mb-3">
  <label for="exampleFormControlInput1" className="form-label">write here</label>
  <input type="email" className="form-control"  id="exampleFormControlInput1" placeholder="" value={textInput} onChange={(e) => setTextInput(e.target.value)} /> 
  <div   >
  <Timer onTimerComplete={handleTimerComplete} />
  </div>
  <b>{textInput.split("").length} characters  and  {Math.ceil(textInput.split("").length/5)} words</b>
</div>
<Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Time out"
      >
        <h2>Timer has reached 0!</h2>
        <button onClick={closeModal}>Close Modal</button>
      </Modal>
    </div>
  )
}

export default WritingPage
