import React, { useState, useEffect, useCallback } from 'react';

const timerContainer = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  height: "10vh",
};

const timer = {
  fontSize: '3em',
  backgroundColor: 'blue',
  color: 'black',
  padding: '20px',
  borderRadius: '10px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
};

const Timer = ({ secondsToBeSend }) => {
  const [seconds, setSeconds] = useState(60);

  useEffect(() => {
    const interval = setInterval(() => {
      setSeconds((prevSeconds) => prevSeconds - 1);
    }, 1000);

    // Clear the interval when the component is unmounted
    return () => clearInterval(interval);
  }, []); // Removed unnecessary dependency array

  const sendDataToParent = useCallback(() => {
    // Call the parent's function with the data
    secondsToBeSend(seconds);
  }, [seconds]);

  useEffect(() => {
    if (seconds === 0) {
      clearInterval(); // Clear the interval using the correct reference
      sendDataToParent();
    }
  }, [seconds, sendDataToParent]); // Removed unnecessary empty dependency array

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const remainingSeconds = time % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  return (
    <div className="timer-container" style={timerContainer}>
      <div className="timer" style={timer}>
        {formatTime(seconds)}
      </div>
    </div>
  );
};

export default Timer;
