import React, { useState, useEffect, useRef } from 'react';

const timerContainer = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  height: "10vh",
};

const timer = {
  fontSize: '3em',
  backgroundColor: 'blue',
  color: 'black',
  padding: '20px',
  borderRadius: '10px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
};

const Timer = ({ onTimerComplete, isStarted }) => {
  const [seconds, setSeconds] = useState(60);
  const intervalRef = useRef();

  useEffect(() => {
    const updateTimer = () => {
      setSeconds(prevSeconds => {
        if (prevSeconds > 0) {
          return prevSeconds - 1;
        } else {
          clearInterval(intervalRef.current);
          onTimerComplete(0);
          return 0;
        }
      });
    };

    if (isStarted) {
      intervalRef.current = setInterval(updateTimer, 1000);
    }

    return () => clearInterval(intervalRef.current);
  }, [isStarted, onTimerComplete]);

  useEffect(() => {
    if (seconds === 0 && isStarted) {
      onTimerComplete(seconds);
    }
  }, [seconds, onTimerComplete, isStarted]);

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const remainingSeconds = time % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  return (
    <div className="timer-container" style={timerContainer}>
      <div className="timer" style={timer}>
        {formatTime(seconds)}
      </div>
    </div>
  );
};

export default Timer;
