import React, { useState, useEffect, useCallback } from 'react';
import Timer from '../components/Timer';
import 'bootstrap/dist/css/bootstrap.min.css';
import Modal from 'react-modal';

const WritingPage = () => {
  const [textInput, setTextInput] = useState("");
  
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isStarted, setIsStarted] = useState(false);
  const [myWord, setMyWord] = useState("");
  const [incorrectCount, setIncorrectCount] = useState(0);
  const [characters,setCharacters] = useState(0)
  const generateRandomText = useCallback(() => {
    const words = [
      "Game", "is", "my", "sit", "Name", "constructor",
      "adipiscing", "chocolate", "sed", "do", "termper", "bestfriend",
      "Fight", "in", "manufacture", "eneyearfeast", "money", "magna", "aliqua"
    ];

    const randomText = Array.from({ length: 250 }, () => words[Math.floor(Math.random() * words.length)]);
    return randomText.join(" ");
  }, []); 

  useEffect(() => {
    if (isStarted) {
      setMyWord(generateRandomText());
      setIncorrectCount(0); 
    }
  }, [isStarted, generateRandomText]);

  useEffect(() => {
    if (isStarted) {
      const minLength = Math.min(myWord.length, textInput.length);
  
      let count = 0;
  
      for (let i = 0; i < minLength; i++) {
        if (myWord[i] !== textInput[i] && myWord[i] !== ' ' && textInput[i] !== ' ') {
          count++;
          
        } else {
          if (myWord[i+1] === ' ') {
            setMyWord(myWord.substring(i + 1));
            
          } else {
            setMyWord(myWord.substring(i + 1));
            setTextInput(textInput.substring(i + 1));
          }
          break;
        }
      }
  
      setIncorrectCount(count);
    }
  }, [isStarted, myWord, textInput]);

  const handleTimerComplete = (value) => {
    

    if (value === 0) {
      setIsModalOpen(true);
      setIsStarted(false);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleStart = () => {
    setIsStarted(true);
  };

  return (
    <div>
      
      <div className="mb-3">
        <label htmlFor="exampleFormControlTextarea1" className="form-label">Typing Speed Test</label>
        <textarea className="form-control" id="exampleFormControlTextarea1" rows="5" value={myWord} disabled={true}>
          


        </textarea>
      </div>
      <div className="mb-3">
        <label htmlFor="exampleFormControlInput1" className="form-label">Write here</label>
        <input
          type="email"
          className="form-control"
          id="exampleFormControlInput1"
          placeholder=""
          value={isStarted ? textInput : ""}
          disabled={!isStarted}
          onChange={(e) => setTextInput(e.target.value)}
        />
        <div>
          {isStarted && <Timer isStarted={isStarted} onTimerComplete={handleTimerComplete} />}
        </div>
        <b>{textInput.split("").length} characters, {Math.ceil(textInput.split("").length / 7)} words</b>
        {isStarted && <p>Incorrect characters: {incorrectCount}</p>}
        {!isStarted && <button onClick={handleStart}>Start Typing Test</button>}
      </div>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Time out"
        style={{
          content: {
            height: '200px',
            width: '400px',
            margin: 'auto', 
          },
          overlay: {
            background: 'rgba(0, 0, 0, 0.3)', 
          },
        }}
      >
        <h2>Timer has reached 0!</h2>
        <button onClick={closeModal}>Close Modal</button>
      </Modal>
    </div>
  );
};

export default WritingPage;
