import React, { useState, useEffect, useCallback } from 'react';
import Timer from '../components/Timer';
import 'bootstrap/dist/css/bootstrap.min.css';
import Modal from 'react-modal';

const WritingPage = () => {
  const [textInput, setTextInput] = useState("");
  const [toPassValue, setToPassValue] = useState();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isStarted, setIsStarted] = useState(false);
  const [myWord, setMyWord] = useState("");
  const [isCorrect, setIsCorrect] = useState(0);

  const generateRandomText = useCallback(() => {
    const words = [
      "Game", "is", "my", "sit", "Name", "constructor",
      "adipiscing", "chocolate", "sed", "do", "termper", "bestfriend",
      "Fight", "in", "manufacture", "eneyearfeast", "money", "magna", "aliqua"
    ];

    const randomText = Array.from({ length: 250 }, () => words[Math.floor(Math.random() * words.length)]);
    return randomText.join(" ");
  }, []); // Empty dependency array to memoize the function

  useEffect(() => {
    if (isStarted) {
      setMyWord(generateRandomText());
      setIsCorrect(null); // Reset correctness when new text is generated
    }
  }, [isStarted, generateRandomText]);

  useEffect(() => {
    // Compare myWord with textInput whenever textInput changes
    if (isStarted) {
      const isEqual = myWord === textInput;
      setIsCorrect(setIsCorrect + 1);
    }
  }, [isStarted, myWord, textInput]);

  const handleTimerComplete = (value) => {
    setToPassValue(value);

    if (value === 0) {
      setIsModalOpen(true);
      setIsStarted(false);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleStart = () => {
    setIsStarted(true);
  };

  return (
    <div>
      <div className="mb-3">
        <label htmlFor="exampleFormControlTextarea1" className="form-label">Typing Speed Test</label>
        <textarea className={`form-control ${isCorrect === false ? 'incorrect' : ''}`} id="exampleFormControlTextarea1" rows="5" value={myWord} disabled={true}></textarea>
      </div>
      <div className="mb-3">
        <label htmlFor="exampleFormControlInput1" className="form-label">Write here</label>
        <input
          type="email"
          className={`form-control ${isCorrect === false ? 'incorrect' : ''}`}
          id="exampleFormControlInput1"
          placeholder=""
          value={isStarted ? textInput : ""}
          disabled={!isStarted}
          onChange={(e) => setTextInput(e.target.value)}
        />
        <div>
          {isStarted && <Timer isStarted={isStarted} onTimerComplete={handleTimerComplete} />}
        </div>
        <b>{textInput.split("").length} characters and {Math.ceil(textInput.split("").length / 7)} words  and correct alphabets are {isCorrect}</b>
        {!isStarted && <button onClick={handleStart}>Start Typing Test</button>}
      </div>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Time out"
        style={{
          content: {
            height: '200px',
            width: '400px',
            margin: 'auto', // Center the modal
          },
          overlay: {
            background: 'rgba(0, 0, 0, 0.3)', // Semi-transparent overlay
          },
        }}
      >
        <h2>Timer has reached 0!</h2>
        <button onClick={closeModal}>Close Modal</button>
      </Modal>
    </div>
  );
};

export default WritingPage;
